mapper.select:
SELECT sds.id, sds.data_source_name, sds.data_source_type, sds.host, sds.port, sds.username, sds.password, sds.connection_params, sds.description, sds.status, sds.created_id, sds.updated_id, su_create.real_name AS created_by_name, su_update.real_name AS updated_by_name, sds.create_time, sds.update_time FROM ubsp_di_sync_data_source sds LEFT JOIN ubsp_sys_user su_create ON sds.created_id = su_create.id AND su_create.is_deleted = 0 LEFT JOIN sys_user su_update ON sds.updated_id = su_update.id AND su_update.is_deleted = 0
sds.is_deleted = 0
AND sds.data_source_name LIKE CONCAT('%', #{queryDTO.dataSourceName}, '%')
AND sds.data_source_type = #{queryDTO.dataSourceType}
AND sds.status = #{queryDTO.status}
